    const [scrollRatio, setScrollRatio] = useState(0);

    useEffect(() => {
        const onScroll = () => {
        const scrollTop = window.scrollY;
        const maxScroll = document.body.scrollHeight - window.innerHeight;
        setScrollRatio(Math.min(scrollTop / maxScroll, 1));
        };
        window.addEventListener("scroll", onScroll);
        return () => window.removeEventListener("scroll", onScroll);
    }, []);

    const count = 10 + Math.floor(scrollRatio * 30); // min 10, max 40

    const sparkles = Array.from({ length: count}, (_, i) => ({
        x: Math.random() * 100,
        y: Math.random() * 40,
        delay: Math.random() * (2 - scrollRatio * 1.5), // faster at bottom
    }));

    return (
        <div className="absolute inset-0 overflow-hidden pointer-events-none z-10">
        {sparkles.map((s, i) => (
            <Sparkle key={i} x={s.x} yOffset={s.y} delay={s.delay} />
        ))}
        </div>
    );
    };

export default SparkleGroup;
